// 4 phases
// 1.initialization --> state & render 
// 2.mounting --> udsate components 
        // 1.componentWillMount()  -- before render componennt forts trigger the ......
        // 2.componentDidMount()  -- upadte state values -- similar to useEffect()
// 3.updating  --> reciept 
      // 1.componentWillUpdate()-->  yes or no ?
      // 2.componentDidUpdate()  --> reciept
// 4.unmount --> delete
// shouldComponentUpdate() --> true,false

import React from "react";
class App extends React.Component{
  constructor(){
    super();
    this.state={
      data:"We are learning React Js"
    }
  }
  componentWillMount(){
    alert("leraning life cycle methods")
  }
  getSnapshotBeforeUpdate(prevProps,prevState){
document.getElementById("para2").innerHTML="preveious state is "+prevState.data
  }
  render(){
    return(
      <div>

          <p>{this.state.data}</p>
          <p id="para"></p>
          <p id="para2"></p>
          <button onClick={this.delete}>Delete</button>

          <h3>{this.state.data}</h3>

      </div>
    )
  }
  componentDidMount(){
    setTimeout(()=>{
      this.setState({
        data:"Front end"
      })
    },2000)
  }
  componentWillUpdate(){
    alert("are you updating the state values???....")
  }
  componentDidUpdate(){
document.getElementById("para").innerHTML="Updated state is "+this.state.data
  }
  shouldComponentUpdate(){
    return true
  }
  delete=()=>{
    this.setState({
      data:false
    })
  }
}

export default App;
