import React from "react";
class App extends React.Component{
  constructor(){
    super();
    this.state={
      data:"We are learning React Js"
    }
  }
  render(){
    return(
      <div>
          <center>
          <p>{this.state.data}</p>
          </center>
      </div>
    )
  }
}

export default App;
