import { useContext, useState } from "react"

// useContext 
// 1.craete context
// 2.provider 
// 3.consumer 
import React from "react"
let Context=React.createContext()

function App(){
  const [val,setVal]=useState("We re learning Hooks Methods")
  return(
    <div>
      <Context.Provider value={val}>
      <h3>Main Component</h3>
      <App1/>
      </Context.Provider>
    </div>
  )
}

function App1(){
  return(
    <div>
      <h3>Firts Component </h3>

      <App2 />

    </div>
  )
}
function App2(){

  return(
    <div>
      <h3>second Component </h3>
      <App3 />
    </div>
  )
}
function App3(){
  let value=useContext(Context)
  return(
    <div>
      <h3>third Component {value}</h3>

    </div>
  )
}

export default App;
